import { useEffect, useState } from "react";
import { getData } from "./api";

const useCSV = () => {
  const [data, setData] = useState([]);
  useEffect(() => {
    const getCsvData = async () => {
      const response = await getData();
      setData(response);
    }
    getCsvData();
  }, [])

  return data;
};

export default useCSV;
